import React, { useRef } from 'react'
import * as THREE from 'three'
import {  useLoader } from 'react-three-fiber'
import img from '../texture/brick.png'
function Plane(props) {
  // This reference will give us direct access to the mesh
  const mesh = useRef()

  const texture = useLoader(THREE.TextureLoader, img)



  return (
    <mesh {...props} ref={mesh} position={[0,0,0]} rotation={[ -Math.PI / 2, 0 , 0]} 
    receiveShadow="true">
      <planeBufferGeometry attach="geometry" args={[10, 10, 10]} />
      <meshStandardMaterial metalness={0.5} attach="material" color="lightblue" />
    </mesh>
  )
}
export default Plane;
